import React, { useState } from 'react';
import { BrowserRouter, Routes, Route, useLocation } from 'react-router-dom';
import './App.css';
import Login from './COMPONENTES/LOGIN/Login';
import ComponenteMenu from "./COMPONENTES/MENU/ComponenteMenu";
import TabelaInscricoes from './COMPONENTES/JOAO/TabelaInscricoes';
import DevsPage from './COMPONENTES/DEVS/DevsPage'; // Importando o novo componente DevsPage

function App() {
  const [isLoggedIn, setLoggedIn] = useState(false);
  const [isMenuExpanded, setMenuExpanded] = useState(false);

  const location = useLocation(); // Hook para obter a rota atual

  const handleLogin = () => {
    setLoggedIn(true);
  };

  return (
    <BrowserRouter>
      {!isLoggedIn ? (
        <Login path='/cadastro' onLogin={handleLogin} />
      ) : (
        <>
          <ComponenteMenu path='/cadastros' isMenuExpanded={isMenuExpanded} setMenuExpanded={setMenuExpanded} />

          {/* Exibe o cabeçalho apenas se NÃO estiver na página "DEVs" */}
          {location.pathname !== "/devs" && (
            <div id='cabecalho' className={isMenuExpanded ? 'expanded' : ''}>
              <div className="main--content">
                <div className="header--wrapper">
                  <div className="header--title">
                    <h3 id='texto'> Sistema de Recrutamento Online - SRO </h3>
                  </div>
                </div>
              </div>
            </div>
          )}

          <Routes>
            {/* Rota para Inscrições */}
            <Route path="/inscricoes" element={<TabelaInscricoes isMenuExpanded={isMenuExpanded} />} />

            {/* Rota para DEVs */}
            <Route path="/devs" element={<DevsPage />} />
          </Routes>
        </>
      )}
    </BrowserRouter>
  );
}

export default App;
