// Login.jsx
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Login.css';

function Login({ onLogin }) {
  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate();

  const handleLogin = async () => {
    const usuario = document.getElementById('usuario').value;
    const senha = document.getElementById('senha').value;

    const response = await fetch('http://localhost:4000/login', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ usuario, senha })
    });

    if (response.ok) {
      const data = await response.json();
      localStorage.setItem('token', data.token);
      setErrorMessage('');
      navigate('/inscricoes');
      onLogin();
    } else {
      setErrorMessage('Usuário ou senha inválidos.');
    }
  };

  return (
    <div id="login">
      <h2 id="titulo">Login</h2>
      <form>
        <div className="row">
          <div className="col-12">
            <div>
              <label id="label" htmlFor="usuario">
                <i className="bi bi-person-fill"></i> Usuário:
              </label>
              <input
                type="text"
                id="usuario"
                name="usuario"
                className="form-control"
                placeholder="Digite o usuário"
                required
              />
              <div className="invalid-feedback">Informe o Usuário</div>
            </div>
          </div>
        </div>
        <div>
          <div>
            <div>
              <label id="label" htmlFor="senha">
                <i className="bi bi-file-lock2"></i> Senha:
              </label>
              <input
                type="password"
                id="senha"
                name="senha"
                className="form-control"
                placeholder="Digite a senha"
                required
              />
            </div>
          </div>
        </div>
        <div id="mensagem">{errorMessage}</div>
        <button type="button" id="botao" onClick={handleLogin}>
          Entrar
        </button>
      </form>
    </div>
  );
}

export default Login;
