import React, { useState, useEffect } from 'react';
import { BrowserRouter, Routes, Route, Navigate } from 'react-router-dom';
import './App.css';
import Login from './COMPONENTES/LOGIN/Login';
import ComponenteMenu from "./COMPONENTES/MENU/ComponenteMenu";
import TabelaInscricoes from './COMPONENTES/JOAO/TabelaInscricoes';
import DevsPage from './COMPONENTES/DevsPage';

function App() {
  const [isLoggedIn, setLoggedIn] = useState(false);
  const [isMenuExpanded, setMenuExpanded] = useState(false);

  // Verifica se há um token no localStorage para manter o estado de login
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      setLoggedIn(true);
    }
  }, []);

  const handleLogin = () => {
    setLoggedIn(true);
  };

  const handleLogout = () => {
    setLoggedIn(false);
    localStorage.removeItem('token'); // Remove o token ao deslogar
  };

  return (
    <BrowserRouter>
      <Routes>
        {/* Se o usuário não estiver logado, redireciona para a página de login */}
        {!isLoggedIn ? (
          <Route path="*" element={<Login onLogin={handleLogin} />} />
        ) : (
          <>
            {/* Menu sempre visível após o login */}
            <ComponenteMenu isMenuExpanded={isMenuExpanded} setMenuExpanded={setMenuExpanded} />
            {/* Rotas protegidas */}
            <Route path="/inscricoes" element={<TabelaInscricoes isMenuExpanded={isMenuExpanded} />} />
            <Route path="/devs" element={<DevsPage />} />
            <Route path="*" element={<Navigate to="/inscricoes" />} />
          </>
        )}
      </Routes>
    </BrowserRouter>
  );
}

export default App;
