import "./TabelaInscricoes.css";
import InscricaoService from '../../SERVICES/inscricoesService.js';
import { useState, useEffect } from 'react';
import InscreverCandidato from "./InscreverCandidato.jsx";

const inscricaoService = new InscricaoService();

function TabelaInscricoes({ isMenuExpanded }) {
  const [inscricoes, setInscricoes] = useState([]);
  const [searchInput, setSearchInput] = useState('');  // Campo de busca para CPF ou Nome
  const [searchType, setSearchType] = useState('cpf'); // Tipo de busca: cpf ou nome
  const [error, setError] = useState(null);
  const [successMessage, setSuccessMessage] = useState(null);
  const [candidatos, setCandidatos] = useState([{
    cpf: 0,
    nome: "Nenhum candidato encontrado"
  }]);
  const [vagas, setVagas] = useState([{
    codigo: 0,
    cargo: "Nenhuma vaga encontrada"
  }]);

  useEffect(() => {
    buscarCandidatos();
    buscarVagas();
    buscarInscricoes();
  }, []);

  const fetchWithErrorHandling = async (url) => {
    try {
      const response = await fetch(url);
      const data = await response.json();
      if (!response.ok) throw new Error(data.message || "Failed to fetch");
      return data;
    } catch (error) {
      console.error("Fetch error:", error.message);
      throw error;
    }
  };

  function buscarCandidatos() {
    fetchWithErrorHandling('http://localhost:3001/candidatos')
      .then(data => setCandidatos(data.listaCandidatos || []))
      .catch(error => {
        setError("Erro ao recuperar candidatos: " + error.message);
        setCandidatos([]);
      });
  }

  function buscarVagas() {
    fetchWithErrorHandling('http://localhost:3001/vagas')
      .then(data => setVagas(data.listaVagas || []))
      .catch(error => {
        setError("Erro ao recuperar vagas: " + error.message);
        setVagas([]);
      });
  }

  function buscarInscricoes() {
    fetchWithErrorHandling('http://localhost:3001/inscricoes')
      .then(data => setInscricoes(data.listaInscricoes || []))
      .catch(error => {
        setError("Erro ao recuperar inscrições: " + error.message);
        setInscricoes([]);
      });
  }

  // Função para restaurar a tabela de matrículas
  const handleRestaurarTabela = async () => {
    setSearchInput(''); // Limpar o campo de pesquisa
    buscarInscricoes(); // Recarregar todas as inscrições
  };

  // Função para buscar por CPF ou nome
  const handleFiltrar = async () => {
    try {
      console.log('Input Pesquisar:', searchInput);

      if (!searchInput) {
        console.log('Sem input pesquisar. Carregar todas inscrições.');
        buscarInscricoes();
        return;
      }

      let filtro = {};

      // Verifica se a busca é por CPF ou nome
      if (/^\d{11}$/.test(searchInput)) {
        filtro = { cpf: searchInput };
      } else {
        filtro = { nome: searchInput };
      }

      const response = await fetch('http://localhost:3001/candidatos/filtrar', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(filtro),
      });

      const matriculasFiltradas = await response.json();

      if (matriculasFiltradas.length === 0) {
        setError('Candidato não encontrado. Verifique o termo de busca e tente novamente.');
        setTimeout(() => {
          setError(null);
        }, 5000);
      } else {
        setInscricoes(matriculasFiltradas);
      }
    } catch (error) {
      console.error('Erro ao filtrar inscrições:', error);
      setError('Erro ao filtrar inscrições. Tente novamente mais tarde.');
      setTimeout(() => {
        setError(null);
      }, 5000);
    }
  };

  return (
    <div id="formularioCandidato" className={isMenuExpanded ? "expanded" : ""}>
      <div className="main--content">
        <div className="form--wrapper">
          <InscreverCandidato></InscreverCandidato>
          <div id='mensagem'>
            {successMessage && (
              <div className="alert alert-success" role="alert">
                <div className='centraliza'>
                  {successMessage}
                </div>
              </div>
            )}
          </div>
          <div className="row">
            <div className="col-6">
              <div className="form-group borda-form mt-5">
                <label htmlFor="pesquisar">
                  <i className="bi bi-search"></i> Pesquisar:
                </label>
                <div className="input-group flex-nowrap">
                  <input
                    type="text"
                    className="form-control"
                    placeholder={`Informe o ${searchType === 'cpf' ? 'CPF' : 'nome'} do candidato`}
                    aria-label="search"
                    aria-describedby="addon-wrapping"
                    name="searchInput"
                    value={searchInput}
                    onChange={(event) => setSearchInput(event.target.value)}
                  />
                </div>
              </div>
            </div>
            <div id='pesquisar'>
              <button
                className="btn btn-primary btn-gradient"
                id="pesquisar"
                type="button"
                onClick={handleFiltrar}
              >
                Pesquisar
              </button>
            </div>
            <div id='restaurar'>
              <button
                className="btn btn-primary btn-gradient"
                id="restaurar"
                type="restaurar"
                onClick={handleRestaurarTabela}
              >
                Restaurar Tabela
              </button>
            </div>
            {error && <div className="alert alert-danger ml-4" role="alert">{error}</div>}
            {inscricoes.length === 0 ? (
              <div className="alert alert-danger ml-4 text-center mx-auto" role="alert">
                ERRO: Não foi possível buscar a lista de inscritos no backend!
              </div>
            ) : (
              <table className="table table-striped table-hover table-bordered">
                <thead className="azul">
                  <tr>
                    <th scope="col">CPF</th>
                    <th scope="col">Candidato</th>
                    <th scope="col">Vaga</th>
                    <th scope="col">Salário</th>
                    <th scope="col">Data da inscrição</th>
                  </tr>
                </thead>
                <tbody>
                  {inscricoes.map(inscricao => {
                    const candidato = candidatos.find(c => c.cpf === inscricao.cand_cpf);
                    const vaga = vagas.find(v => v.codigo === inscricao.vaga_codigo);

                    return (
                      <tr key={inscricao.cand_cpf}>
                        <td className="texto">{inscricao.cand_cpf}</td>
                        <td className="texto">{candidato ? candidato.nome : 'Desconhecido'}</td>
                        <td className="texto">{vaga ? vaga.cargo : 'Desconhecido'}</td>
                        <td className="texto">R$ {vaga ? vaga.salario : 'Desconhecido'}</td>
                        <td className="texto">{inscricao.data_inscricao}</td>
                      </tr>
                    );
                  })}
                </tbody>
              </table>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default TabelaInscricoes;
