// Login.jsx
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Login.css';

function Login({ onLogin }) {
  const [usuario, setUsuario] = useState('');
  const [senha, setSenha] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate();

  const handleLogin = async () => {
    try {
      // Faz a requisição para o backend
      const response = await fetch('http://localhost:4000/', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ usuario, senha }) // Envia os dados do login
      });

      console.log('Resposta do servidor:', response); // Log para verificar a resposta do backend

      if (response.ok) {
        const data = await response.json();
        console.log('Token recebido:', data.token); // Log para exibir o token JWT recebido
        localStorage.setItem('token', data.token); // Armazena o token no localStorage
        setErrorMessage('');
        navigate('/inscricoes'); // Redireciona para a página de inscrições
        onLogin(); // Chama a função de login se houver
      } else {
        setErrorMessage('Usuário ou senha inválidos.'); // Exibe mensagem de erro caso o login falhe
      }
    } catch (error) {
      setErrorMessage('Erro ao tentar fazer login. Por favor, tente novamente.'); // Captura erros de conexão ou outros problemas
    }
  };

  return (
    <div id="login">
      <h2 id="titulo">Login</h2>
      <form>
        <div className="row">
          <div className="col-12">
            <div>
              <label id="label" htmlFor="usuario">
                <i className="bi bi-person-fill"></i> Usuário:
              </label>
              <input
                type="text"
                id="usuario"
                name="usuario"
                className="form-control"
                placeholder="Digite o usuário"
                value={usuario}
                onChange={(e) => setUsuario(e.target.value)} // Atualiza o estado do usuário
                required
              />
              <div className="invalid-feedback">Informe o Usuário</div>
            </div>
          </div>
        </div>
        <div>
          <div>
            <div>
              <label id="label" htmlFor="senha">
                <i className="bi bi-file-lock2"></i> Senha:
              </label>
              <input
                type="password"
                id="senha"
                name="senha"
                className="form-control"
                placeholder="Digite a senha"
                value={senha}
                onChange={(e) => setSenha(e.target.value)} // Atualiza o estado da senha
                required
              />
            </div>
          </div>
        </div>
        <div id="mensagem">{errorMessage}</div>
        <button type="button" id="botao" onClick={handleLogin}>
          Entrar
        </button>
      </form>
    </div>
  );
}

export default Login;
