import React, { useState, useEffect } from 'react';
import { BrowserRouter, Routes, Route, Navigate } from 'react-router-dom';
import './App.css';
import Login from './COMPONENTES/LOGIN/Login';
import ComponenteMenu from "./COMPONENTES/MENU/ComponenteMenu";
import TabelaInscricoes from './COMPONENTES/JOAO/TabelaInscricoes';
import DevsPage from './COMPONENTES/DevsPage'; // Importando o novo componente DevsPage

function App() {
  // Verifica se há um token no localStorage para manter o estado de login
  const [isLoggedIn, setLoggedIn] = useState(!!localStorage.getItem('token'));
  const [isMenuExpanded, setMenuExpanded] = useState(false);

  const handleLogin = () => {
    setLoggedIn(true);
    localStorage.setItem('isLoggedIn', 'true'); // Salva o estado de login no localStorage
  };

  const handleLogout = () => {
    setLoggedIn(false);
    localStorage.removeItem('token'); // Remove o token ao deslogar
  };

  // useEffect para verificar se o usuário está logado ao carregar o componente
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      setLoggedIn(false);
    }
  }, []);

  return (
    <BrowserRouter>
      {!isLoggedIn ? (
        <Routes>
          <Route path="*" element={<Login onLogin={handleLogin} />} />
        </Routes>
      ) : (
        <>
          <ComponenteMenu path='/cadastros' isMenuExpanded={isMenuExpanded} setMenuExpanded={setMenuExpanded} />
          <Routes>
            {/* Rota para Inscrições */}
            <Route path="/inscricoes" element={<TabelaInscricoes isMenuExpanded={isMenuExpanded} />} />

            {/* Rota para DEVs */}
            <Route path="/devs" element={<DevsPage />} />

            {/* Redireciona para a página de inscrições por padrão */}
            <Route path="*" element={<Navigate to="/inscricoes" />} />
          </Routes>
        </>
      )}
    </BrowserRouter>
  );
}

export default App;
